export default class App extends Component {
    state = { value: "" };
    handleChange = (event) => {
        this.setState({
            [event.target.name]: event.target.value });
        console.log(this.state.value)
    }
    render() {
        return ( <
            label htmlFor = "name" > Name:
            <
            input id = "name"
            name = "name"
            onChange = { this.handleChange }
            /> < /
            label >
        );
    }
}
